mapper.select:
select  a.*,b.name teacherName from t_course a,t_teacher b


		and ID = #{Course.id}


		and b.ID = #{Course.teacherId}


		and b.NAME like '%${Course.teacherName}%'


		and a.NAME like '%${Course.name}%'


		and a.CYEAR = #{Course.cyear}


		and a.SCH_TERM = #{Course.schTerm}


		and a.CREDIT = #{Course.credit}


		and a.STATE = #{Course.state}



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}



		and COURSE_ID = #{Course.courseId}

	 and a.teacher_id = b.id


	order by ${order} ${sort}

mapper.update:
update t_course
TEACHER_ID = #{teacherId, jdbcType=NUMERIC}, NAME = #{name, jdbcType=VARCHAR}, CYEAR = #{cyear, jdbcType=NUMERIC}, SCH_TERM = #{schTerm, jdbcType=NUMERIC}, CREDIT = #{credit, jdbcType=NUMERIC}, STATE = #{state, jdbcType=NUMERIC}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, COURSE_ID = #{courseId, jdbcType=NUMERIC}, ID = #{id}
ID = #{id}
