mapper.select:
select   from t_menu


		and ID = #{Menu.id}


		and NAME like '%${Menu.name}%'


		and URL = #{Menu.url}


		and PID = #{Menu.pid}


		and MLEVEL = #{Menu.mlevel}


		and ORDER_NO = #{Menu.orderNo}



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}




	order by ${order} ${sort}

mapper.update:
update t_menu
NAME = #{name, jdbcType=VARCHAR}, URL = #{url, jdbcType=VARCHAR}, PID = #{pid, jdbcType=NUMERIC}, MLEVEL = #{mlevel, jdbcType=NUMERIC}, ORDER_NO = #{orderNo, jdbcType=NUMERIC}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, ID = #{id}
ID = #{id}
