mapper.select:
select a.*,b.name className from t_student a,t_class b


		and ID = #{Student.id}


		and a.NAME like '%${Student.name}%'


		and a.SEX = #{Student.sex}



		and BIRTHDAY >= #{birthdayBegin} 




		and BIRTHDAY <= #{birthdayEnd}



		and SNATIVE = #{Student.snative}


		and b.CLASS_ID = #{Student.classId}


		and b.name like '%${Student.className}%'



		and CREATE_DATE >= #{createDateBegin} 




		and CREATE_DATE <= #{createDateEnd}


		and a.class_id = b.id


	order by ${order} ${sort}

mapper.update:
update t_student
NAME = #{name, jdbcType=VARCHAR}, SEX = #{sex, jdbcType=NUMERIC}, BIRTHDAY = #{birthday, jdbcType=TIMESTAMP}, SNATIVE = #{snative, jdbcType=VARCHAR}, CLASS_ID = #{classId, jdbcType=NUMERIC}, CREATE_DATE = #{createDate, jdbcType=TIMESTAMP}, ID = #{id}
ID = #{id}
